function validateForm(){if(""==document.getElementById("name").value)return document.querySelector(".status").innerHTML="Name cannot be empty",!1;var e=document.getElementById("email").value;if(""==e)return document.querySelector(".status").innerHTML="Email cannot be empty",!1;return/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e)?""==document.getElementById("subject").value?(document.querySelector(".status").innerHTML="Subject cannot be empty",!1):""==document.getElementById("message").value?(document.querySelector(".status").innerHTML="Message cannot be empty",!1):void(document.querySelector(".status").innerHTML="Sending..."):(document.querySelector(".status").innerHTML="Email format invalid",!1)}